import React, {useState} from 'react'
import Notes from './Notes/Notes';
import './App.css';
import AddNoteForm from './AddNoteForm/AddNoteForm';
import NoteContainer from './NoteContainer/NoteContainer';

function App() {
  const [notes,setNotes] = useState([])
  const[id,setId] = useState('')
  const [title,setTitle] = useState('')
  const [content,setContent] = useState('')
  const [error,setError] = useState(false)
  const [creating,setCreating] = useState(false)
  const [showNote,setShowNote] = useState(false)

  const handleChange_title = (e) => {
    setTitle(e.target.value)
  }
  const handleChange_content = (e) => {
    setContent(e.target.value)
  }

  const saveNoteHandler = (e) =>{
    e.preventDefault()
    if (title.length === 0 || content.length === 0) {
      setError(true)
    } else{
      setError(false)
      const note = {
        id : new Date(),
        title : title,
        content : content
      }
      const updatedNotes = [...notes,note]
      setNotes(updatedNotes)
    }
    setTitle('')
    setContent('')
  }

  const handleAddNote = () => {
    setCreating(true)
    setTitle('')
    setContent('')
  }

  const noteClicked = (id) => {
    setCreating(false)
    let selectedNote = notes.find((note)=> note.id === id)
    setContent(selectedNote.content)
    setTitle(selectedNote.title)
    setShowNote(true)
  }

  const deleteNoteHandler = (id) =>{
    const findIndex = notes.findIndex(note => note.id === id )
    console.log('====================================');
    console.log(findIndex)
  }

  return (
    <div className='app'>

      <Notes className='notes'
      notes={notes}
      handleAddNote={handleAddNote}
      noteClicked={noteClicked}
      />

      {
        !creating 

        ? 
        <NoteContainer className=''
        title = {title}
        content = {content}
        showNote = {showNote}
        deleteNoteHandler = {deleteNoteHandler}
        /> 

        : 
        <AddNoteForm className='add-note-form' 
            saveNoteHandler={saveNoteHandler}
            handleChangeContent={handleChange_content}
            handleChangeTitle={handleChange_title}
            error={error}
            title={title}
            content={content}
          />
      }

    </div>
  );
}

export default App;
