import React from 'react'
import './AddNoteForm.css';

function AddNoteForm() {


  return (
    <form>
        <input className='title-content input' type='text' placeholder='title'onChange={handleChange}/>
        <textarea className='note-content input' type='text' placeholder='text' rows='8' onChange={handleChange}/>
        <input className='input-btn input' type='button' value='Save' onClick={handleClick}/>
    </form>
  )
}

export default AddNoteForm
