import React,{useState} from 'react'
import './Notes.css'
import Note from './Note'

function Notes() {
  const arr = ["A","B","C","D"]
  const [count,setCount] = useState(0)
  const handleAdd = () => {
    if (count < arr.length) {
      setCount(count+1)
    }
  }
  return (
    <div className='notes'>
      {
  arr.map((el, i) => {
     if (i < count) {
       <Note title={el} key={i} i={i} />
    }
  })
}
      <button className='add-btn' onClick={handleAdd}>+</button>
    </div>
  )
}

export default Notes
